        while check(&a) == None {
            cnt += 1;
            a = sim(a);
        }
        let res = check(&a).unwrap();
        match cnt & 1 {
            1 => !res,
            _ => res,
        }
    };
    println!("{}", if ok { "First" } else { "Second" });
}

