use std::cmp::{min, max};

fn main() {
    input!{
        n: usize,
        mut v: [usize; n]
    }
    let mut f = true;
    loop {
        let es = v.iter().filter(|&x| x % 2 == 0).count();
        if es % 2 == 1 {
            println!("{}", if f {"First"} else {"Second"});
            return;
        } else if n - es > 1 {
            println!("{}", if f {"Second"} else {"First"});
            return;
        }
        let mut g = 0;
        for i in 0..n {
            if v[i] == 1 {
                println!("{}", if f {"Second"} else {"First"});
                return;
            }
            if v[i] % 2 == 1 {
                v[i] -= 1;
            }
            g = gcd(g, v[i]);
        }
        for i in 0..n {
            v[i] /= g;
        }
        f ^= true;
    }
}

fn gcd(n: usize, m: usize) -> usize {
    let mut l = min(n, m);
    let mut r = max(n, m);
    while l > 0 {
        let t = l;
        l = r % l;
        r = t;
    }
    r
}

