#define drp(i,st,ed) for (int i=st;i>=ed;--i)

const int MOD=1e9+7;
const int N=4005;

int f[N][N],s[N][N];

int add(int x,int y) {
	x+=y; (x>=MOD)?(x-=MOD):0;
	return x;
}

int dec(int x,int y) {
	x+=MOD-y; (x>=MOD)?(x-=MOD):0;
	return x;
}

int ksm(int x,int dep) {
	int res=1;
	for (;dep;dep>>=1,x=1LL*x*x%MOD) {
		(dep&1)?(res=1LL*res*x%MOD):0;
	}
	return res;
}

int main(void) {
	int n,k; scanf("%d%d",&n,&k);
	f[0][n+1]=1;
	drp(j,n+1,1) s[0][j]=add(s[0][j+1],f[0][j]);
	rep(i,1,k) {
		rep(j,1,n-i+1) f[i][j]=s[i-1][j];
		drp(j,n+1,1) s[i][j]=add(s[i][j+1],f[i][j]);
	}
	int ans=dec(f[k][1],f[k-1][1]);
	ans=ans*ksm(2,n-k-1)%MOD;
	printf("%d\n", ans);
	return 0;
}

