	int N,K; cin >> N >> K; K--;

	V<mint> dp = {1};
	rep(i,K){
		V<mint> ndp(i+2);
		rep(x,i+1){
			ndp[x+1] += dp[x];
//			rep1(v,x) ndp[v] += dp[x];
		}
		mint s = 0;
		per1(x,i){
			s += dp[x];
			ndp[x] += s;
		}
		swap(dp,ndp);
	}

	mint ans = 0;
	rep(x,K+1){
		ans += dp[x] * Choose(x+N-1-K,x);
	}
