inline char nc() {
    // return getchar();
    static char buf[100000], *l = buf, *r = buf;
    return l==r&&(r=(l=buf)+fread(buf,1,100000,stdin),l==r)?EOF:*l++;
}
template<class T> void read(T &x) {
    x = 0; int f = 1, ch = nc();
    while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=nc();}
    while(ch>='0'&&ch<='9'){x=x*10-'0'+ch;ch=nc();}
    x *= f;
}
typedef long long LL;
const int mod = 1e9 + 7;
const int maxn = 2000 + 50;
int n, k;
int f[maxn][maxn][2];
inline int add(int x) {return x >= mod ? x - mod : x;}
LL power(LL x, LL y) {
    LL re = 1;
    while(y) {
        if(y & 1) re = re * x % mod;
        x = x * x % mod;
        y >>= 1;
    }
    return re;
}
int solve() {
    f[n][0][0] = f[n][1][1] = 1;
    for(int i = n; i >= 1; --i) {
        for(int j = n; j >= 0; --j) {
            f[i][j][0] = add(f[i][j][0] + f[i][j + 1][0]);
            for(int t = 0; t < 2; ++t) {
                f[i - 1][j][0] = add(f[i - 1][j][0] + f[i][j][t]);
                f[i - 1][j + 1][1] = add(f[i - 1][j + 1][1] + f[i][j][t]);
            }
        }
    }
    return f[1][n - k][0] * power(2, max(n - k - 1, 0)) % mod;
}
int main() {
    read(n), read(k);
    printf("%d\n", solve());
    return 0;
}

