const int mod = 1e9 + 7;
const int nmax = 2e3 + 10;

int n, k;
int dp[nmax];

int sum(int a, int b) {
    a += b; if (a >= mod) a -= mod; return a;
}

int mul(int a, int b) {
    return (1LL * a * b) % mod;
}

int lgpow(int a, int b) {
    if (b <= 0) return 1;
    int res = 1;
    for (int i = 0; (1 << i) <= b; ++i) {
        if (b & (1 << i))
            res = mul(res, a);
        a = mul(a, a);
    }
    return res;
}

int main()
{
    scanf("%d %d", &n, &k);
    dp[0] = 1;
    for (int i = n - 1; i; --i)
        for (int j = n - i + 1; j >= 0; --j)
            dp[j] = sum(dp[j-1], dp[j+1]);

    int ans = mul(dp[n-k], lgpow(2, n - k - 1));
    printf("%d\n", ans);
    return 0;
}

