      if (i == 0) {
        dp[i][j] = i + j == n - m;
      } else {
        add(dp[i][j], pre[i + j - 1]);
      }
      if (i + j == n - 1) add(ret, dp[i][j]);
      add(pre[i + j], dp[i][j]);
    }
  }
  for (int i = 0; i < n - m - 1; ++i) {
    ret = ret * 2 % MOD;
  }
  cout << ret << endl;
  return 0;
}

