inline LL pow(LL x, LL exp) {
  if (exp < 0) return 1;
  LL res = 1;
  for (; exp; exp >>= 1, x = x * x % mod)
    if (exp & 1) res = res * x % mod;
  return res;
}

inline void pre() {
  for (int i = 0; i < N; i ++) {
    C[i][0] = 1;
    for (int j = 1; j <= i; j ++) {
      C[i][j] = C[i - 1][j - 1] + C[i - 1][j];
      if (C[i][j] >= mod) C[i][j] -= mod;
    }
  }
}

inline LL DP(int n) {
  pref[0] = 1;
  for (int i = 2; i <= n; i ++) {
    for (int j = 1; j < i; j ++) {
      pref[j] = pref[j - 1] + dp[i - 1][j];
      if (pref[j] >= mod) pref[j] -= mod;
      dp[i][j] = pref[j];
    }
  }
  LL ans = 1;
  for (int i = 1; i <= n; i ++) {
    ans += dp[n][i];
    if (ans >= mod) ans -= mod;
  }
  return ans;
}

