            if (j+1 <= N) dp[i+1][j+1] -= dp[i][j];
        }
        rep(j,N) dp[i+1][j+1] += dp[i+1][j];
        for (int j = 2; j <= i; j++) dp[i+1][j+1] += dp[i][j];
        dp[i+1][0] += dp[i][0];
        for (int j = 2; j <= i+1; j++) dp[i+1][j] += dp[i][0];
    }

    ModCombination MC(100000);
    mint val = 2;
    if (N==K) val = 1;
    else val ^= (N-K-1);
    mint ans = MC.get(N-1,N-K);
    for (int i = 2; i <= K-1; i++) ans += dp[K-1][i]*MC.get(N-1-(K-i),N-K);

    ans *= val;
