mint inv[MAX],fac[MAX],finv[MAX];

void make(){

    fac[0]=fac[1]=1;
    finv[0]=finv[1]=1;
    inv[1]=1;

    for(int i=2;i<MAX;i++){
        inv[i]=-inv[mod%i]*(mod/i);
        fac[i]=fac[i-1]*i;
        finv[i]=finv[i-1]*inv[i];
    }
}

mint comb(ll a,ll b){
    if(a<b) return 0;
    return fac[a]*finv[b]*finv[a-b];
}

mint perm(ll a,ll b){
    if(a<b) return 0;
    return fac[a]*finv[a-b];
}

mint dp[MAX][MAX],S[MAX][MAX];

int main(){

    std::ifstream in("text.txt");
    std::cin.rdbuf(in.rdbuf());
    cin.tie(0);
    ios::sync_with_stdio(false);

    make();

    int N,K;cin>>N>>K;

    if(N==1){
        cout<<1<<endl;
        return 0;
    }

    if(K==N) K--;
    mint ans=0;

    dp[0][0]=1;
    for(int i=0;i<=N;i++){
        for(int j=0;j<=N;j++){
            dp[i+1][j+1]+=dp[i][j];
        }
        for(int j=1;j<=N;j++){
            dp[i+1][j]+=S[i][i]-S[i][j-1];
        }
        for(int j=1;j<=N;j++) S[i+1][j]=S[i+1][j-1]+dp[i+1][j];
    }

    for(int Y=N-K+1;Y<=N;Y++){
        int gomi=(Y-1)-(N-K);

        mint sum=0;
        for(int j=0;j<=N-Y;j++){
            sum+=comb(j+gomi,gomi)*dp[N-Y][j];
        }
        ans+=sum*comb(Y-2,N-K-1);
    }

    ans*=mint(2).pow(N-K-1);

    cout<<ans.val()<<endl;

}

