int binpow(int a,int t)
{
	int res=1,p=a;
	for (int i=t;i;i>>=1)
	{
		if (i&1) res=1ll*res*p%mod;
		p=1ll*p*p%mod;
	}
	return res;
}
void Init()
{
	jc[0]=injc[0]=1;
	for (int i=1;i<=3000;i++)
	{
		jc[i]=1ll*jc[i-1]*i%mod;
		injc[i]=1ll*injc[i-1]*binpow(i,mod-2)%mod;
	}
}
int C(int n,int k)
{
	if (n<k) return 0;
	return 1ll*jc[n]*injc[k]%mod*injc[n-k]%mod;
}
