}

typedef mint_base<> mint;


map<pair<int, int>, mint>memo;
mint dp(int s, int t)
{
	if (memo.count({ s,t }))return memo[{s, t}];
	if (s < t || t < 0)return 0;
	return memo[{s, t}] = dp(s - 1, t) + dp(s, t - 1);
}

int main(void)
{
	int N, K;
	cin >> N >> K;
	int dif = N - K - 1;
	dif = max(dif, 0);
	vector<mint>vec;
	fact_set<1000000007>(vec, 2345);
	auto comb = [&vec](int n, int k)
	{
		if (n < k || n < 0 || k < 0)return mint(0);
		return vec[n] * ~vec[k] * ~vec[n - k];
	};
	mint ans = 0;
	memo[{0, 0}] = 1;
	for (int m = 0; m <= N; ++m)
	{
		mint res = comb(m - 2, dif);
		res *= m_pow<1000000007>(2, dif);
		res *= dp(K - 1, N - m);
		ans += res;
	}
	cout << ans << endl;
	return 0;
}

