LL fpm(LL base, LL exp)
{
    LL ret = 1;
    for (; exp; exp >>= 1) {
        if (exp & 1)
            (ret *= base) %= MOD;
        (base *= base) %= MOD;
    }
    return ret;
}

LL fac[MAXN], ifac[MAXN], pow2[MAXN];

inline LL binom(int n, int k)
{
    assert(0 <= k && k <= n);
    return fac[n] * ifac[k] % MOD * ifac[n - k] % MOD;
}

void solve()
{
    fac[0] = 1;
    for (int i = 1; i <= N; ++i) {
        fac[i] = fac[i - 1] * i % MOD;
    }
    ifac[N] = fpm(fac[N], MOD - 2);
    for (int i = N; i >= 1; --i) {
        ifac[i - 1] = ifac[i] * i % MOD;
    }

    static LL dp[MAXN][MAXN];

    for (int i = 0; i <= N; ++i) {
        dp[i][0] = 1;
        for (int j = 1; j < i; ++j) {
            dp[i][j] = (dp[i - 1][j] + dp[i][j - 1]) % MOD;
        }
    }

    int ans = 0;
    for (int i = 0; i < K; ++i) {
        (ans += dp[K - 1][i] * binom(N - 1 - i, K - 1 - i) % MOD) %= MOD;
    }
    for (int i = 0; i < N - K - 1; ++i) {
        (ans *= 2) %= MOD;
    }

    printf("%d\n", ans);
}

