  int n, k; cin >> n >> k;

  Graph<int> dp(k, vi(n, 0));
  rep(i,n) dp[0][i] = 1;

  rep1(i,k) {
    int sum = dp[i-1][n-i];
    rrep(j,n-i) {
      dp[i][j] = (sum += dp[i-1][j]) %= MOD;
    }
  }

  ll ans = dp[k-1][0] - (k == 1 ? 0 : dp[k-2][0]);
  if (ans < 0) ans += MOD;

  if (k < n) ans = ans * modpow(2, n - k - 1) % MOD;

  cout << ans << endl;
}


