  if(k < 0) return 1;
  if(k == 0) return 1;
  int tmp = 1LL;
  while(k) {
    if(k & 1) tmp = 1LL * tmp * x % MOD;
    x = 1LL * x * x % MOD;
    k >>= 1;
  }
  return tmp;
}

