ll modExp(ll e, ll n, ll m = MOD){
	ll ret = 1;
	for (; n; n >>= 1, e = e * e % m)
		if (n & 1)
			ret = ret * e % m;
	return ret;
}

inline void update(int &x, int y){
	if ((x += y) >= MOD)
		x -= MOD;
}

