		int s = f[i - 1][i - 1];
		for (int j = i; j < n; ++j){
			update(f[i][j], s);
			update(f[i][j], f[i - 1][j]);
			update(s, f[i - 1][j]);
		}
	}
	int ans = 0;
	for (int i = 0; i < n; ++i)
		update(ans, f[k - 1][i]);
	printf("%d\n", (int)(ans * modExp(2, n == k ? 1 : n - k - 1) % MOD));
