const int MOD=1000000007;
const int MAXN=2000;


int n,pos;
int p2[MAXN+1];
int dp[MAXN+1][MAXN+1][2];

int bf() {
	set<vector<int> > res;
	REP(i,1<<(n-1)) REP(j,1<<(n-1)) {
		deque<int> q;
		q.push_back(0); REP(k,n-1) if(i&(1<<k)) q.push_back(k+1); else q.push_front(k+1);
		vector<int> cur;
		REP(k,n-1) if(j&(1<<k)) cur.PB(q.back()),q.pop_back(); else cur.PB(q.front()),q.pop_front(); cur.PB(q.back()),q.pop_back();
		if(cur[pos]!=0) continue;
		res.insert(cur);
	}
	//for(set<vector<int> >::iterator it=res.begin();it!=res.end();++it) { printf("sol:"); REPSZ(i,*it) printf(" %d",(*it)[i]); puts(""); }
	return SZ(res);
}

int solve() {
	p2[0]=1; FORE(i,1,n) p2[i]=(ll)p2[i-1]*2%MOD;
	int nafter=n-pos-1;
	memset(dp,0,sizeof(dp));
	REPE(j,n-1) {
		if(j>=nafter) dp[1][j][1]=nafter==0?1:p2[nafter-1];
		if(j==nafter) dp[1][j][0]=nafter==0?1:p2[nafter-1];
	}
	FORE(i,2,n) REPE(j,n-i) {
		dp[i][j][0]=((ll)dp[i-1][j+1][0]+dp[i-1][j][1])%MOD;
		dp[i][j][1]=((ll)dp[i-1][j+1][0]+dp[i-1][j][1]+(j==0?0:dp[i][j-1][1]))%MOD;
	}
	//REPE(i,n) REPE(j,n-i) REP(k,2) if(dp[i][j][k]!=0) printf("dp[%d][%d][%d]=%d\n",i,j,k,dp[i][j][k]);
	int ret=dp[n][0][0];
	/*if(n<=10) {
		int chk=bf();
		if(chk!=ret) printf("have %d want %d\n",ret,chk);
	}*/
	return ret;
}

void run() {
	scanf("%d%d",&n,&pos); --pos;
	printf("%d\n",solve());

}

int main() {
	run();
	return 0;
}

