void doDp (int N)
{
    dp[N][0] = 1;
    for (int i=N; i>1; i--)
        for (int j=N - i + 1; j>=0; j--)
            if (dp[i][j])
            {
                ///dau push, si apoi eventual mai scot cateva numere din lista si le bag si pe alea
                for (int p=0; p<=j; p++)
                    dp[i - 1][j - p] = add (dp[i - 1][j - p], dp[i][j]);
                ///daca adaug unu pe lista, fix la momentul asta, imi permit sa si scot
                dp[i - 1][j + 1] = add (dp[i - 1][j + 1], dp[i][j]);
            }
}

int solve (int N, int K)
{
    int ans = mul (dp[1][N - K], put2[max (N - K - 1, 0)]);
    return ans;
}

