ll fac[maxn], inv[maxn], facinv[maxn], bin[maxn];
ll f[maxn][maxn];
void pre()
{
	fac[0] = 1; for(int i = 1; i < maxn; ++i) fac[i] = fac[i - 1] * i % mod;
	inv[1] = 1; for(int i = 2; i < maxn; ++i) inv[i] = (mod - mod / i) * inv[mod % i] % mod;
	facinv[0] = 1; for(int i = 1; i < maxn; ++i) facinv[i] = facinv[i - 1] * inv[i] % mod;
	bin[0] = 1; for(int i = 1; i < maxn; ++i) bin[i] = bin[i - 1] * 2 % mod;
}
ll C(int n, int m) { return n < m ? 0 : fac[n] * facinv[m] % mod * facinv[n - m] % mod;}
int main()
{
	pre();
	int n, k;
	scanf("%d%d", &n, &k);
	for(int j = 1; j <= n; ++j) f[1][j] = 1;
	for(int i = 2; i < k; ++i)
	{
		ll sum = 0;
		for(int j = n; j; --j)
		{
			if(n - j + 1 > i - 1) f[i][j] = f[i - 1][j];
			f[i][j] = (f[i][j] + sum) % mod;
			sum = (sum + f[i - 1][j]) % mod;
		}
	}
	ll ans = 0;
	if(k > 1) for(int j = 2; j <= n; ++j) ans = (ans + f[k - 1][j]) % mod;
	else ans = 1;
	if(k < n) ans = ans * bin[n - k - 1] % mod;
