    dp[0][N] = 1;

    for (int len = 0; len < K; len++) {
        mod_int sum = 0;

        for (int under = N - len - 1; under >= 0; under--) {
            sum += dp[len][under + 1];
            dp[len + 1][under] = sum;
        }

        for (int minimum = 1; minimum < N - len; minimum++)
