			(dp[i - 1][j][0] += dp[i][j][0] + dp[i][j][1]) %= MOD;
			(dp[i - 1][j + 1][1] += dp[i][j][0] + dp[i][j][1]) %= MOD;
			if (j > 0) (dp[i][j - 1][0] += dp[i][j][0]) %= MOD;
		}
	}
	ll ret = (dp[1][N - K][0] + dp[1][N - K][1]) % MOD;
	if (N - K - 1 >= 0) (ret *= mod_pow(2, N - K - 1)) %= MOD;
	cout << ret << endl;
}

