      int sum = m - 2 , choose = n - K - 1;
      if(sum < choose) continue;
      int tmp = ll(comb[sum][choose]) * f[n-m][m-2-choose] % P * pw2[choose] % P;
      pp(ans , tmp);
    }
    cout << ans << endl;
  }
  return 0;
}

