int mem[2][2222][2222];

int dp(int shit, int cur, int left) {
	if(cur < 1) return 0;
	if(cur == 1 && ((n - 1 - left) == k-1)) {
		return 1;
	}
	if(mem[shit][cur][left] != -1) {
		return mem[shit][cur][left];
	}
	long long ans = 0;
	ans += dp(0, cur - 1, left);
	if(left && !shit) {
		ans += dp(0, cur, left - 1);
	}
	ans += dp(1, cur - 1, left + 1);
	ans %= mod;
	return mem[shit][cur][left] = ans;
} 

