		ll now = 0;
		for (int j = 1; j <= i - 1; j++)
		{
			dp[i][j] = (dp[i][j] + now) % mod;
			now += dp[i - 1][j];
			now %= mod;
		}
		for (int j = 1; j <= i - 1; j++)dp[i][j] = (dp[i][j] + com(num - kai + i - 1, num - kai)) % mod;
		for (int j = 0; j <= i; j++)dp[i][j] = (dp[i][j] + dp[i - 1][j]) % mod;
		//for (int j = 0; j <= i; j++)printf("%lld ", dp[i][j]); printf("\n");
	}
