int bp(int x, int d){
	if(!d){
		return 1;
	}
	if(d&1){
		return x * 1LL * bp(x, d-1) % MOD;
	}
	int r = bp(x, d/2);
	return r * 1LL * r % MOD;
}

void recalcPref(int id){
	for(int i=0;i<N;++i){
		pref[id][i] = dp[id][i];
		if(i > 0){
			add(pref[id][i], pref[id][i-1]);
		}
	}
}

int getPref(int l, int r, int id){
	int ret = pref[id][r];
	if(l > 0){
		ret -= pref[id][l-1];
		if(ret < 0){
			ret += MOD;
		}
	}
	return ret;
}

