int Qpow2(int k){
	if (k==-1){
		return 1;
	}
	ll x=2,ans=1LL;
	while (k){
		if (k&1){
			ans=(ans*x)%mod;
		}
		x=(x*x)%mod;
		k/=2;
	}
	return (int)ans;
}

