ll n, k, f[MAXN];
inline ll fpm(ll a, ll b = mo - 2)
{
	ll res = 1;
	while (b)
	{
		if (b & 1) res = res * a % mo;
		a = a * a % mo;
		b >>= 1;
	}
	return res;
}

int main()
{
