	f[0] = 1ll;
	for(int i = 1; i < n; ++i)
	{
		for(int j = i; j; --j)f[j] = (f[j + 1] + f[j - 1]) % mo;
		f[0] = f[1];
	}
	printf("%lld", f[n - k] * fpm(2, n - k - 1) % mo);
	return 0;
}

