const int P=1000000007;
inline int Pow(int a,int b){
    int c=1;
    for(;b;b>>=1,a=a*1ll*a%P)if(b&1)c=c*1ll*a%P;
    return c;
}
inline void add(int &a,int b){a+=b;if(a>=P)a-=P;}
inline void dec(int &a,int b){a-=b;if(a<0)a+=P;}
int n,k;
int C[2*N][2*N];
int f[N][N];
int main(){
    scanf("%d%d",&n,&k);
    rep(i,0,2*n){
        C[i][0]=C[i][i]=1;
        rep(j,1,i-1){
            C[i][j]=C[i-1][j];
            add(C[i][j],C[i-1][j-1]);
        }
    }
    f[0][0]=1;
    rep(i,0,n){
        if(i){rep(j,1,n)add(f[i][j],f[i][j-1]);}
        rep(j,0,n)if(f[i][j]){
            dec(f[i+1][j+2],f[i][j]);
            add(f[i+1][1],f[i][j]);
        }
    }
    if(n==k){
        int ans=0;
        rep(i,0,n-1)add(ans,f[n-1][i]);
        printf("%d\n",ans);
    }
    int ans=0;
    rep(i,n-k+1,n){
        rep(j,0,n-i){
            add(ans,(f[n-i][j]*1ll*C[i-1-(n-k)+j][j]%P)*1ll*C[i-2][n-k-1]%P);
        }
        //printf("__%d\n",ans);
    }
    ans=ans*1ll*Pow(2,n-k-1)%P;
    printf("%d\n",ans);
    return 0;
}

