long long modpow(long long a, long long n,long long mod=MOD){
    long long i=1,ret=1,p=a;
    while(i<=n){
        if(i&n) ret=(ret*p)%mod;
        i=(i<<1);
        p=(p*p)%mod;
    }
    return ret;
}
vector<long long> fac(3030),two(3030),inv(3030);

LL modcomb(LL a, LL b){
    if(a==0 and b==-1){
        return 1;
    }else if(a<b){
        return 0;
    }else{
        return (fac[a]*inv[b]%MOD)*inv[a-b]%MOD;
    }
}
LL pow2(LL a){
    if(a==-1)return 1;
    else return two[a];
}

