    if (y % 2) re l * l % MOD * x % MOD;
    re l * l % MOD;
}

ll rev(ll x)
{
    return inq(x, MOD - 2);
}

bool __precomputed_combinatorics = 0;
vector<ll> __fact, __ufact, __rev;

void __precompute_combinatorics()
{
    __precomputed_combinatorics = 1;
    __fact.resize(N);
    __ufact.resize(N);
    __rev.resize(N);
    __rev[1] = 1;
    for (int i = 2; i < N; i++) __rev[i] = MOD - __rev[MOD % i] * (MOD / i) % MOD;
    __fact[0] = 1, __ufact[0] = 1;
    for (int i = 1; i < N; i++) __fact[i] = __fact[i - 1] * i % MOD, __ufact[i] = __ufact[i - 1] * __rev[i] % MOD;
}

ll fact(int x)
{
    if (!__precomputed_combinatorics) __precompute_combinatorics();
    return __fact[x];
}

ll cnk(int n, int k)
{
    if (!__precomputed_combinatorics) __precompute_combinatorics();
    return __fact[n] * __ufact[n - k] % MOD * __ufact[k] % MOD;
}

ll n, k;
ll dp[N];

ll uni[N];

ll get_by_dp(ll mp, ll n)
{
    dp[0] = 1;
    for (int i = 1; i <= n + 1; i++)
    {
        uni[i] = dp[i - 1];
        dp[i - 1] = (dp[i - 1] + (mp - 1) * uni[i - 1]) % MOD;
        dp[i] = 0;
        for (int last = 1; last <= i; last++)
        {
            dp[i] = (dp[i] + dp[i - last] * uni[last]) % MOD;
        }
    }
    return dp[n];
}

