		for (int j = n; j >= 0; j --) {
			(dp[i - 1][j][0] += dp[i][j][0] + dp[i][j][1]) %= MOD;
			(dp[i - 1][j + 1][1] += dp[i][j][0] + dp[i][j][1]) %= MOD;
			if (j > 0) {
				(dp[i][j - 1][0] += dp[i][j][0]) %= MOD;
			}
		}
	}
	long long ans = dp[1][n - k][0] + dp[1][n - k][1];
	(ans *= (n == k ? 1 : Pow(2, n - k - 1))) %= MOD;
	printf("%lld\n", ans);
