void solve() {
	//cout << dp[3][1] << endl;
	int n, k; cin >> n >> k;
	if (k == 1) {
		cout << mod_pow(2,n-2) << endl; return;
	}
	ll ans = 0;
	for (int x = 0; x <= k-1; x++) {
		ll csum = p.comb(n - x - 2, k - 1 - x);
		//p.comb(k - 1, x) % mod;

		csum = csum * rdp[k-1][x+1] % mod;
		csum = csum * mod_pow(2, n - k-1) % mod;
		ans += csum; ans %= mod;
	}
	cout << ans << endl;
}


