ll dp(int i, int left, bool ok){
	int used = n-i  - left;
	if(left < 0)	return 0;
	if(used > k)	return 0;
	if(i == 1 && used != k){
		ll ans = 0;
		if(left > 0 && ok)
			ans = dp(1, left-1, true);
// cout<<"dp "<<i<<" "<<left<<" "<<ok<<" returns "<<ans<<endl;
		return ans;
	}
	if(used == k || i == 1){
// cout<<"dp "<<i<<" "<<left<<" "<<ok<<" returns 1"<<endl;
		return 1;
	}
	ll &ans = DP[i][left][ok];
	if(ans != -1)	return ans;

	ans = dp(i-1, left, true) + dp(i-1, left+1, false);
	ans %= MOD;
	if(left > 0 && ok){
		ans += dp(i, left-1, true);
		ans %= MOD;
	}
// cout<<"dp "<<i<<" "<<left<<" "<<ok<<" returns "<<ans<<endl;
	return ans;
}


