            dp[i + 1][j] %= mod;
        }
    }
    int ans = 0;
    FOR(j, 1, N){
        ans += dp[K - 2][j];
        ans %= mod;
    }
    if(K < N){
        ans *= modpow(2, N - K - 1, mod);
        ans %= mod;
    }
    //debug(dp);
    cout << ans << endl;

    return 0;
}

