LL fpm(LL base, LL exp)
{
    LL ret = 1;
    for (; exp; exp >>= 1) {
        if (exp & 1)
            (ret *= base) %= MOD;
        (base *= base) %= MOD;
    }
    return ret;
}

LL fac[MAXN], ifac[MAXN], pow2[MAXN];

inline LL C(int n, int k)
{
    return n < k ? 0 : fac[n] * ifac[k] % MOD * ifac[n - k] % MOD;
}

void solve()
{
    int bnd = N * 2;
    fac[0] = 1;
    for (int i = 1; i <= bnd; ++i) {
        fac[i] = fac[i - 1] * i % MOD;
    }
    ifac[bnd] = fpm(fac[bnd], MOD - 2);
    for (int i = bnd; i >= 1; --i) {
        ifac[i - 1] = ifac[i] * i % MOD;
    }

    int ans = 0;
    for (int i = 0; i < K; ++i) {
        (ans += (C(K-2+i, K-2) - C(K-2+i, K-1) + MOD) * C(N-1-i, K-1-i) % MOD) %= MOD;
    }
    for (int i = 0; i < N - K - 1; ++i) {
        (ans *= 2) %= MOD;
    }

    printf("%d\n", ans);
}

