		num *= rev[N - M];
		num %= MOD;
		long long int add = 1;
		add *= by[M - 1 + i];
		add %= MOD;
		add *= rev[i];
		add %= MOD;
		add *= rev[M - 1];
		add %= MOD;
		add *= M - i;
		add %= MOD;
		add *= power(M, MOD - 2, MOD);
		add %= MOD;
		//cout << "i= " << i << endl;
		//cout << num << " " << add << endl;
		add += MOD-bef;
		add %= MOD;
		bef += add;
		bef %= MOD;
		num *= add;
		ans += num;
		ans %= MOD;
	}
	if (N - M - 1 >= 0)ans *= power(2, N - M - 1, MOD);
	ans %= MOD;
	cout << ans << endl;
}

