#define mod 1000000007

using namespace std;

struct SegTree{
	int size;
	vector<llint> seg;

	SegTree(){}
	SegTree(int size){
		this->size = size;
		seg.resize(1<<(size+1));
	}

	void init()
	{
		for(int i = 0; i < (1<<(size+1)); i++) seg[i] = 0;
	}

	void update(int i, llint val)
	{
		i += (1 << size);
		seg[i] = val;
		while(i > 1){
			i /= 2;
			seg[i] = (seg[i*2]+seg[i*2+1]) % mod;
		}
	}

	llint query(int a, int b, int k, int l, int r)
	{
		if(b < l || r < a) return 0;
		if(a <= l && r <= b) return seg[k];
		llint lval = query(a, b, k*2, l, (l+r)/2);
		llint rval = query(a, b, k*2+1, (l+r)/2+1, r);
		return (lval+rval)%mod;
	}
	llint query(int a, int b)
	{
		if(a > b) return 0;
		return query(a, b, 1, 0, (1<<size)-1);
	}
};

llint fact[2005], fact_inv[2005];

llint pow(llint a, llint n)
{
	if(n == 0) return 1;
	if(n % 2){
		return ((a%mod) * (pow(a, n-1)%mod)) % mod;
	}
	else{
		return pow((a*a)%mod, n/2) % mod;
	}
}

void make_fact()
{
	llint val = 1;
	fact[0] = 1;
	for(int i = 1; i < 2005; i++){
		val *= i;
		val %= mod;
		fact[i] = val;
	}
	for(int i = 0; i < 2005; i++){
		fact_inv[i] = pow(fact[i], mod-2);
	}
}

llint comb(llint n, llint k)
{
	llint ret = 1;
	ret *= fact[n];
	ret *= fact_inv[k], ret %= mod;
	ret *= fact_inv[n-k], ret %= mod;
	return ret;
}

llint N, K;
SegTree seg[2] = {SegTree(12), SegTree(12)};

int main(void)
{
	make_fact();
	cin >> N >> K;
	if(N == 1){
		cout << 1 << endl;
		return 0;
	}

	seg[0].update(0, 1);
	for(int i = 1; i <= K-1; i++){
		seg[i%2].init();
		for(int j = 0; j <= N-1; j++){
			llint tmp = 0;
			tmp += seg[(i+1)%2].query(j+1, j+1);
			if(N-(i+j) > 0) tmp += seg[(i+1)%2].query(j, j), tmp %= mod;
			if(j <= N-1-i) tmp += seg[(i+1)%2].query(0, j-1), tmp %= mod;
			seg[i%2].update(j, tmp);
		}
	}

	llint ans = 0;
	for(int i = 0; i <= N-1; i++){
		ans += seg[(K-1)%2].query(i, i) * pow(2, max(N-K-1, 0LL)) % mod;
		ans %= mod;
	}
	cout << ans << endl;
	return 0;
}

