int n, k;
int dp[maxn][maxn];

void solve(){
    int power = 1;
    for(int i = 1;i <= n - k - 1;i++){
        power = power * 2 % mod;
    }
    memset(dp, 0, sizeof(dp));
    dp[n + 1][0] = 1;
    for(int i = n;i >= 1;i--){
        for(int j = 0;j <= n;j++){
            add(dp[i][j + 1], dp[i + 1][j]);
        }
        for(int j = n;j >= 1;j--){
            add(dp[i][j - 1], dp[i][j]);
        }
    }
    int ans = 1LL * dp[2][n - k] * power % mod;
    cout << ans << endl;
}

