    dp[0][n] = 1;
    for (int i = 0; i < m; i++) {
        for (int j = 0; j < n-i; j++) {
            dp[i+1][j] += dp[i][j];
        }
        mint sum = 0;
        for (int j = n-i; j >= 0; j--) {
