void MAIN() {
    scanf("%d%d", &N, &K);

    dp[0][0] = 1;

    REP (i, N) {
	if (i+1 == K) {
	    REP (j, N) {
		int rest = N-i-j;
		if (rest <= 0) break;
		dp[i+1][j+rest-1] += dp[i][j];
	    }
	} else {
	    memset(aux, 0, sizeof aux);
	    REP (j, N) {
		int rest = N-i-j;
		if (rest < 0) break;

		if (rest) {
		    aux[j] += dp[i][j];
		    aux[j+rest] -= dp[i][j];
		}

		if (rest || j == 1) {
		    dp[i+1][j-1] += dp[i][j];
		} else {
		    dp[i+1][j-1] += dp[i][j] + dp[i][j];
		}
	    }

	    REP (j, N) aux[j+1] += aux[j];
	    REP (j, N+1) dp[i+1][j] += aux[j];
	}
    }

    Mint ans = dp[N][0].geti();
    printf("%d\n", ans.geti());
}

