int Calc(int n,int m) {
	int fact=1;
	for (int i=1;i<=n;++i) fact=1ll*fact*i%Mod;
	inv[1]=inv[0]=1; for (int i=2;i<=n;++i) inv[i]=1ll*(Mod-Mod/i)*inv[Mod%i]%Mod; 
	for (int i=2;i<=n;++i) inv[i]=1ll*inv[i-1]*inv[i]%Mod;
	return 1ll*fact*inv[m]%Mod*inv[n-m]%Mod; 
