ll dpdp(int R,int B) {
	if(R==0 && B==0) return 1;
	if(R<0 || B<0) return 0;
	ll& ret=dp[R][B];
	if(ret>=0) return ret;

	ret=dpdp(R,B-1);
	int i;
	FOR(i,R) ret += dpdp(i,B+R-(i+1));

	ret %= mo;
	return ret;
}

