const int mod = 1e9 + 7;
const int MN = 2010;

int N, K;

int cc[MN][MN][2];
int dp(int n, int mn, int t) {
    if(n == N) return 1;
    int &ret = cc[n][mn][t];
    if(ret != -1) return ret;
    if(n == K - 1) return ret = mn == 1? 0 : dp(n + 1, 1, 1);

    ret = 0;
    if(t) {
        int cnt = N - mn - n + 1;
        if(cnt > 0) {
            ret += dp(n + 1, mn, 1);
            ret %= mod;
            if(mn == 1 && cnt > 1) {
                ret += dp(n + 1, mn, 1);
                ret %= mod;
            }
        }
        if(mn > 1) {
            ret += dp(n, mn - 1, 0);
            ret %= mod;
        }
    }
    else {
        ret += dp(n + 1, mn, 1);
        ret %= mod;
        if(mn > 1) {
            ret += dp(n, mn - 1, 0);
            ret %= mod;
        }
    }

    //cout << n << ' ' << mn << ' ' << ret << endl;

    return ret;
}

