		return x * 1LL * bp(x, d-1) % MOD;
	}
	int r = bp(x, d/2);
	return r * 1LL * r % MOD;
}

void recalcPref(int id){
	for(int i=0;i<N;++i){
		pref[id][i] = dp[id][i];
		if(i > 0){
			add(pref[id][i], pref[id][i-1]);
		}
	}
}

int getPref(int l, int r, int id){
	if(l > r){
		return 0;
	}
	int ret = pref[id][r];
	if(l > 0){
		ret -= pref[id][l-1];
		if(ret < 0){
			ret += MOD;
		}
	}
	return ret;
}

void solve(){
	if(k == 1 || k == n){
		printf("1\n");
		return;
	}
	dp[1][k-1] = bp(2, n - k - 1);
	for(int i=2;i<=n;++i){
		recalcPref(i-1);
		for(int j=0;j<=n;++j){
			add(dp[i][j], getPref(j, n-i+1, i-1));
		}
	}

	printf("%d\n",dp[n][0]);
}

