long long quickpow(long long a,long long b,long long n)
{
	if(b==0&&n>1) return 1;
	if(b==0&&n==1) return 0;
	if(b==1) return a%n;
	if(b%2==0)
	{
		long long t=quickpow(a,b/2,n);
		return t*t%n;
	}
	else
	{
		long long t=quickpow(a,b/2,n);
		t=t*t%n;
		return t*a%n;
	}
}

