	repp(i,1,K){
		LL s = 0;
		repp(j,0,i){
			(s += dp[j][i-j-1]) %= mod;
			dp[j][i-j] = s;
		}
		dp[i][0] = s;
	}
	LL ans = 0;
	if(N==K){
		ans = dp[N-1][0];
	} else {
		repp(i,0,K){
			(ans += comb(N-i-2,N-K-1) * dp[i][K-i-1] % mod) %= mod;
		}
		repm(i,N-K-1,0){
			(ans += ans) %= mod;
		}
	}
	printf("%lld\n" , ans);
	return 0;
}

