using namespace std;

//#define FILE_IO

typedef long long LL;

const int mod = 1e9 + 7;
const int NMAX = 10005;

int N, K;
int fct[NMAX], ifct[NMAX], p2[NMAX];

int power(int x, int y)
{
    if(y <= 0)  return 1;
    int ans = power( (1LL * x * x) % mod, y >> 1 );
    if(y & 1) ans = (1LL * ans * x) % mod;
    return ans;
}

int pw2(int x) { if(x < 0) return 1; return p2[x]; }

int C(int N, int K)
{
    if(N < K)   return 0;
    int ans = (1LL * fct[N] * ifct[K]) % mod;
    ans = (1LL * ans * ifct[N - K]) % mod;
    return ans;
}

void pre(int N)
{
    fct[0] = 1;
    for(int i = 1; i <= N; i++) fct[i] = (1LL * fct[i - 1] * i) % mod;

    ifct[N] = power(fct[N], mod - 2);
    for(int i = N - 1; i >= 0; i--) ifct[i] = (1LL * ifct[i + 1] * (i + 1)) % mod;

    p2[0] = 1;
    for(int i = 1; i <= N; i++) p2[i] = (2LL * p2[i - 1]) % mod;
}

int getNumberOfWays(int N, int M)
{
    if(N == 0)  return 1;
    M++;
    if(N == 1)  return M;
    int ans = (1LL * fct[M + 2 * N - 1] * ifct[M + N]) % mod;
    ans = (1LL * ans * M) % mod;
    ans = (1LL * ans * ifct[N]) % mod;
    return ans;
}

int main()
{
    #ifdef FILE_IO
    freopen("1.in", "r", stdin);
    freopen("1.out", "w", stdout);
    #endif

    pre(10000);

    scanf("%d%d", &N, &K);
    if(N == K)
    {
        printf("%d\n", getNumberOfWays(N - 1, 0));
        return 0;
    }

    int ans = 0;
    for(int i = 0; i < K; i++)
    {
        int sol = getNumberOfWays(i, K - 1 - i);
        sol = (1LL * sol * C(N - 2 - i, K - 1 - i)) % mod;
        ans = (ans + sol) % mod;
    }
    ans = (1LL * ans * pw2(N - K - 1)) % mod;
    printf("%d\n", ans);

    return 0;
}

