    rep(i,0,k-1){
        ll cn=nck(n-i-2,k-i-1,mod);
        ll su=0;
        rep(j,0,i+1){
            ll u=k-j-2;
            ll t=nck(u,i-j,mod);
            t*=kata[j];
            t%=mod;
            su+=t;
            su%=mod;
        }
        su*=cn;
        su%=mod;
        sum+=su;
        sum%=mod;
    }
    sum+=kata[k-1];
    sum%=mod;
    ll r;
    if(k==n)r=1;
    else r=beki(2,n-k-1,mod);
    sum*=r;
    sum%=mod;
    cout<<sum<<endl;
}

